import React, { useEffect, useState } from "react";
import "./write.css";
import "../../App.css";
import { useNavigate, useLocation } from "react-router-dom";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import axios from "axios";
import { Button, Input, Paper } from "@mui/material";
import SearchField from "../../modules/components/searchField.js";
import LoginPageButton from "../login/loginPageButton.js";
import LogoutIcon from "@mui/icons-material/Logout";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import { useSearchParams } from "react-router-dom";

function BeforeLogin(props) {
  const navigate = useNavigate();

  return (
    <div
      className="bar"
      style={{ display: "flex", justifyContent: "flex-end" }}
    >
      <SearchField />
      <LoginPageButton
        onClick={() => {
          navigate("./login");
        }}
      />
    </div>
  );
}

function AfterLogin(props) {
  const navigate = useNavigate();
  return (
    <div
      className="bar"
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "right",
        margin: "auto",
        padding: "10px",
      }}
    >
      <SearchField />
      <img
        src={sessionStorage.picture}
        style={{
          width: "40px",
          height: "40px",
          borderRadius: "100%",
          marginRight: "16px",
          cursor: "pointer",
        }}
        onClick={() => {
          navigate("/profile");
        }}
        alt={"..."}
      />
      <p
        style={{ fontSize: "18px", cursor: "pointer" }}
        onClick={() => {
          navigate("/profile");
        }}
      >
        {sessionStorage.getItem("name")}
      </p>
      {/* name 가져와 표시 */}
      <LogoutIcon
        style={{ marginLeft: "15px", cursor: "pointer" }}
        onClick={() => {
          sessionStorage.clear();
          window.location.reload();
        }}
      />
    </div>
  );
}

export default function Write() {
  // 이미지 업로드 객체
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);

  const navigate = useNavigate();
  const location = useLocation();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [login, setLogin] = useState(false);

  const [searchParams] = useSearchParams();
  const queryValue = searchParams.get("locationid") || "";
  useEffect(() => {
    if (queryValue !== "") {
      axios
        .post(
          "https://beyhjxqxv3.execute-api.us-east-2.amazonaws.com/default/2023-c-capstone-DAO",
          {
            DML: "SELECT",
            columns: "*",
            table: "location",
            where: `id=${queryValue}`,
          },
        )
        .then((res) => {
          setPreviewImage(res.data[0].image);
          setTitle(res.data[0].title);
          setContent(res.data[0].content);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [queryValue]);

  useEffect(() => {
    // token 여부에 반응하여 로그인 여부 판단
    const token = sessionStorage.getItem("id");
    if (token) {
      setLogin(true);
    } else {
      setLogin(false);
    }
  }, []);

  const handleImageChange = (event) => {
    const selectedFile = event.target.files[0];
    setSelectedImage(selectedFile);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    let selectedImageBase64, fileName;
    if (selectedImage === null) {
      selectedImageBase64 = ``;
      fileName = "sampleImageDamgom.jpeg";
    } else {
      selectedImageBase64 = await convertImageToBase64(selectedImage);
      fileName = selectedImage.name;
    }
    // Perform upload logic here, e.g. using APIs or other methods
    if (queryValue !== "") {
      /** 게시글 업로드 api 추가 **/
      axios
        .post(
          "https://r9d6nxucae.execute-api.us-east-2.amazonaws.com/default/2023-c-capstone-upload",
          {
            /**API JSON 형식 참조하여 post 요청을 보내주세요**/
            type: "post-update",
            fileName: fileName, // 저장할 파일명
            file: JSON.stringify(selectedImageBase64), // 파일 값
            name: location.state.location, // 지역명(seoul, jeju...)-
            id: queryValue,
            title: title, // 게시글 제목
            content: content, // 게시글 내용
          },
        )
        // 문제가 없을 경우 이전 페이지(지역 페이지)로 라우팅
        .then((res) => {
          navigate(`/board/${location.state.location}`);
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      /** 게시글 업로드 api 추가 **/
      axios
        .post(
          "https://r9d6nxucae.execute-api.us-east-2.amazonaws.com/default/2023-c-capstone-upload",
          {
            /**API JSON 형식 참조하여 post 요청을 보내주세요**/
            type: "post",
            fileName: fileName, // 저장할 파일명
            file: JSON.stringify(selectedImageBase64), // 파일 값
            name: location.state.location, // 지역명(seoul, jeju...)
            user_id: sessionStorage.id, // 사용자 id(test@test.com...)
            title: title, // 게시글 제목
            content: content, // 게시글 내용
          },
        )
        // 문제가 없을 경우 이전 페이지(지역 페이지)로 라우팅
        .then((res) => {
          navigate(`/board/${location.state.location}`);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  const convertImageToBase64 = (image) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (event) => {
        resolve(event.target.result.split(",")[1]);
      };
      reader.onerror = (error) => {
        reject(error);
      };
      reader.readAsDataURL(image);
    });
  };
  return (
    <div className="write_cover">
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static" sx={{ backgroundColor: "#045369" }}>
          <Toolbar>
            <Typography
              variant="h4"
              component="div"
              sx={{ flexGrow: 1, fontFamily: "dohyeon" }}
              onClick={() => {
                navigate("/");
              }}
            >
              대동유어지도
            </Typography>
            {login ? <AfterLogin /> : <BeforeLogin />}
          </Toolbar>
        </AppBar>
      </Box>
      <div className="write_main">
        <div className="write_con">
          <div className="top">
            <h4 style={{ margin: "auto", display: "flex" }}>새 게시물</h4>
            <Button
              style={{ position: "absolute", right: "0%", padding: "18px" }}
              variant="contained"
              color="primary"
              onClick={handleUpload}
            >
              업로드
            </Button>
          </div>
          <hr />

          <div className={"middle"}>
            <Input type="file" accept="image/*" onChange={handleImageChange} />
            {previewImage && (
              <Paper sx={{ height: "100%" }} elevation={3}>
                <img
                  src={previewImage}
                  alt="Preview"
                  style={{ maxWidth: "100%", height: "100%" }}
                />
              </Paper>
            )}
          </div>
          <div className="title" style={{ margin: "20px auto auto auto" }}>
            <p>제목 :</p>

            <Box
              component="form"
              sx={{
                "& > :not(style)": { m: 1, width: "600px" },
              }}
              noValidate
              autoComplete="off"
            >
              <TextField
                value={title !== "" ? title : () => {}}
                onChange={(e) => setTitle(e.target.value)}
                id="standard-basic"
                variant="standard"
                style={{ margin: "52px 0px 0px 20px" }}
              />
            </Box>
          </div>
          <div className="bottom">
            <CKEditor
              data={content !== "" ? content : () => {}}
              editor={ClassicEditor}
              config={{ placeholder: "내용을 입력해주세요" }}
              onReady={(editor) => {
                // You can store the "editor" and use when it is needed.
                console.log("Editor is ready to use!", editor);
              }}
              onChange={(event, editor) => {
                setContent(editor.getData());
                // console.log({ event, editor, content });
              }}
              onBlur={(event, editor) => {
                // console.log( 'Blur.', editor );
              }}
              onFocus={(event, editor) => {
                // console.log( 'Focus.', editor );
              }}
            />
          </div>
        </div>
        <div className="write_map">지도</div>
      </div>
    </div>
  );
}
